        }

    ]
    constructor(){
            super();
            this.state={
                articles : this.articles,
                loading : false
            }
        }

    async componentDidMount(){
        let url="https://newsapi.org/v2/top-headlines?country=us&apiKey=f7444080d87a40aca9559ffdce51da3e";
        let data = await fetch(url);
        let datajson= await data.json();
        console.log(datajson);
        this.setState({articles : datajson.articles})
    }

    render() {

        return (
            <div className="container my-3">
                <h2>Top Headlines .. </h2>
                <div className="row">
                    {this.state.articles.map((element)=>{
                        return <div className="col-md-4" key={element.url}>
                        <NewsItems title={!element.title?"":element.title} description={element.description.slice(0,80)} imageUrl={!element.urlToImage?"https://fox8.com/wp-content/uploads/sites/12/2021/12/AP21300808623855.jpg?w=1280":element.urlToImage} newsUrl={element.url}/>
                    </div>
                    })}
                </div>
            </div>
        )
    }
}

export default News
